mapper.select:
SELECT
FROM sys_user AS u RIGHT JOIN ( SELECT user_id AS ids FROM sys_user WHERE mail = #{sysUser.mail,jdbcType=VARCHAR} UNION SELECT user_id AS ids FROM sys_user WHERE telephone = #{sysUser.telephone,jdbcType=VARCHAR} UNION SELECT user_id AS ids FROM sys_user WHERE username = #{sysUser.username,jdbcType=VARCHAR} ) AS t ON u.user_id = t.ids WHERE u.user_id IS NOT NULL
AND u.user_id != #{sysUser.userId,jdbcType=INTEGER}

mapper.insert:
insert into sys_user (user_id, username, password, salt,
  telephone, mail, dept_id, 
  status, remark, create_time, 
  operator, operate_time, operate_ip
  )
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{salt,jdbcType=VARCHAR}
  #{telephone,jdbcType=VARCHAR}, #{mail,jdbcType=VARCHAR}, #{deptId,jdbcType=INTEGER}, 
  #{status,jdbcType=SMALLINT}, #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{operator,jdbcType=VARCHAR}, #{operateTime,jdbcType=TIMESTAMP}, #{operateIp,jdbcType=VARCHAR}
  )
